{
    "openapi": "3.0.2",
    "info": {
        "title": "Topology",
        "version": "1.0.0",
        "description": "This is an API for defining a topology in VIA AIOps.\n\nCopyright (c) 2022, Vitria, Inc. All rights reserved.",
        "termsOfService": "https://www.vitria.com/terms-of-use/",
        "contact": {
            "name": "Vitria API Team",
            "url": "https://help.vitria.com/",
            "email": "support@vitria.com"
        },
        "license": {
            "name": "Vitria End User License Agreement",
            "url": "https://www.vitria.com/eula"
        }
    },
    "servers": [
        {
            "url": "https://localhost:30303/v1",
            "description": ""
        }
    ],
    "paths": {
        "/links": {
            "get": {
                "tags": [
                    "links"
                ],
                "parameters": [
                    {
                        "name": "limit",
                        "description": "maximum number of results to return",
                        "schema": {
                            "format": "int32",
                            "type": "integer"
                        },
                        "in": "query",
                        "required": false
                    }
                ],
                "responses": {
                    "default": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/error"
                                }
                            }
                        },
                        "description": "unexpected error"
                    },
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "items": {
                                        "$ref": "#/components/schemas/output-link"
                                    }
                                },
                                "examples": {
                                    "moderate complex link example": {
                                        "value": [
                                            {
                                                "link-id": "cloud-ncs540-1 : HundredGigE0/0/0/28 : ron-8201-2 : HundredGigE0/0/0/26 : IPv4",
                                                "link-type": "IPv4",
                                                "network": "Default-network",
                                                "source-node": "cloud-ncs540-1",
                                                "source-tp": "HundredGigE0/0/0/28",
                                                "dest-node": "ron-8201-2",
                                                "dest-tp": "HundredGigE0/0/0/26"
                                            },
                                            {
                                                "link-id": "cst-8201-p3 : Bundle-Ether28 : cst-pa2 : Bundle-Ether28 : IPv4",
                                                "link-type": "IPv4",
                                                "network": "Default-network",
                                                "source-node": "cst-8201-p3",
                                                "source-tp": "Bundle-Ether28",
                                                "dest-node": "cst-pa2",
                                                "dest-tp": "Bundle-Ether28"
                                            }
                                        ]
                                    }
                                }
                            }
                        },
                        "description": "links response"
                    }
                },
                "operationId": "getlinks",
                "summary": "List all links",
                "description": "Gets a list of all `link` entities."
            },
            "post": {
                "requestBody": {
                    "description": "Link to add to the topology",
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/input-link"
                            },
                            "examples": {
                                "rich link example": {
                                    "value": {
                                        "link-id": "cloud-ncs540-1 : HundredGigE0/0/0/28 : ron-8201-2 : HundredGigE0/0/0/26 : IPv4",
                                        "link-type": "IPv4",
                                        "network": "Default-network",
                                        "source-node": "cloud-ncs540-1",
                                        "source-tp": "HundredGigE0/0/0/28",
                                        "dest-node": "ron-8201-2",
                                        "dest-tp": "HundredGigE0/0/0/26",
                                        "dims": {
                                            "speed": 100000000
                                        }
                                    }
                                }
                            }
                        }
                    },
                    "required": true
                },
                "tags": [
                    "links"
                ],
                "responses": {
                    "default": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/error"
                                }
                            }
                        },
                        "description": "unexpected error"
                    },
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/input-link"
                                },
                                "examples": {
                                    "rich link example": {
                                        "value": {
                                            "link-id": "cloud-ncs540-1 : HundredGigE0/0/0/28 : ron-8201-2 : HundredGigE0/0/0/26 : IPv4",
                                            "link-type": "IPv4",
                                            "network": "Default-network",
                                            "source-node": "cloud-ncs540-1",
                                            "source-tp": "HundredGigE0/0/0/28",
                                            "dest-node": "ron-8201-2",
                                            "dest-tp": "HundredGigE0/0/0/26",
                                            "dims": {
                                                "speed": 100000000
                                            }
                                        }
                                    }
                                }
                            }
                        },
                        "description": "link response"
                    }
                },
                "operationId": "createlink",
                "summary": "Create a link",
                "description": "Creates a new instance of a `link`."
            }
        },
        "/links/link={link-id}": {
            "get": {
                "tags": [
                    "links"
                ],
                "parameters": [
                    {
                        "name": "link-id",
                        "description": "The identifier for the link to read, update or delete.",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "default": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/error"
                                }
                            }
                        },
                        "description": "unexpected error"
                    },
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/output-link"
                                },
                                "examples": {
                                    "simple link": {
                                        "value": {
                                            "link-id": "cloud-ncs540-1 : HundredGigE0/0/0/28 : ron-8201-2 : HundredGigE0/0/0/26 : IPv4",
                                            "link-type": "IPv4",
                                            "network": "Default-network",
                                            "source-node": "cloud-ncs540-1",
                                            "source-tp": "HundredGigE0/0/0/28",
                                            "dest-node": "ron-8201-2",
                                            "dest-tp": "HundredGigE0/0/0/26"
                                        }
                                    }
                                }
                            }
                        },
                        "description": "links response"
                    }
                },
                "operationId": "getlink",
                "summary": "Get a link.",
                "description": "Gets the details of a single instance of a `link`"
            },
            "put": {
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/base-link"
                            }
                        }
                    },
                    "required": true
                },
                "tags": [
                    "links"
                ],
                "responses": {
                    "202": {
                        "description": "Successful response."
                    }
                },
                "operationId": "updatelink",
                "summary": "Update a link.",
                "description": "Updates a single 'link' based on the link-id supplied"
            },
            "delete": {
                "tags": [
                    "links"
                ],
                "parameters": [
                    {
                        "name": "link-id",
                        "description": "The identifier for the link to read, update or delete.",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "default": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/error"
                                }
                            }
                        },
                        "description": "unexpected error"
                    },
                    "204": {
                        "description": "link deleted"
                    }
                },
                "operationId": "deletelink",
                "summary": "Delete a link.",
                "description": "Deletes a single 'link' based on the link-id supplied"
            },
            "parameters": [
                {
                    "name": "link-id",
                    "description": "The identifier for the link to read, update or delete.",
                    "schema": {
                        "type": "string"
                    },
                    "in": "path",
                    "required": true
                }
            ]
        },
        "/nodes": {
            "get": {
                "tags": [
                    "nodes"
                ],
                "parameters": [
                    {
                        "name": "limit",
                        "description": "maximum number of results to return",
                        "schema": {
                            "format": "int32",
                            "type": "integer"
                        },
                        "in": "query",
                        "required": false
                    }
                ],
                "responses": {
                    "default": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/error"
                                }
                            }
                        },
                        "description": "unexpected error"
                    },
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "items": {
                                        "$ref": "#/components/schemas/output-node"
                                    }
                                },
                                "examples": {
                                    "rich node array": {
                                        "value": [
                                            {
                                                "node-id": "cloud-ncs540-1",
                                                "neighbors": [
                                                    "cloud-ncs540-1",
                                                    "ron-ncs540-1"
                                                ],
                                                "network-id": "Default-network",
                                                "aliases": [
                                                    "100.0.0.50",
                                                    "cloud-ncs540-1.fqdn.com"
                                                ],
                                                "valid-from": "2022-02-10T00:13:13.000+00:00",
                                                "dims": {
                                                    "city": "Boston",
                                                    "state": "MA"
                                                }
                                            },
                                            {
                                                "node-id": "cst-8201-p3",
                                                "neighbors": [
                                                    "cst-8201-p3",
                                                    "ron-8201-2",
                                                    "cst-pa1"
                                                ],
                                                "network-id": "Default-network",
                                                "aliases": [
                                                    "101.0.0.9",
                                                    "cst-8201-p3.fqdn.com"
                                                ],
                                                "dims": {
                                                    "city": "Boise",
                                                    "state": "ID"
                                                }
                                            }
                                        ]
                                    }
                                }
                            }
                        },
                        "description": "nodes response"
                    }
                },
                "operationId": "getnodes",
                "summary": "List all nodes.",
                "description": "Gets a list of all `node` entities."
            },
            "post": {
                "requestBody": {
                    "description": "Node to add to the topology",
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/network-input-node"
                            },
                            "examples": {
                                "rich new node": {
                                    "value": {
                                        "node-id": "cloud-ncs540-1",
                                        "aliases": [
                                            "100.0.0.50",
                                            "cloud-ncs540-1.fqdn.com"
                                        ],
                                        "dims": {
                                            "city": "Boston",
                                            "state": "MA"
                                        }
                                    }
                                }
                            }
                        }
                    },
                    "required": true
                },
                "tags": [
                    "nodes"
                ],
                "responses": {
                    "default": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/error"
                                }
                            }
                        },
                        "description": "unexpected error"
                    },
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/network-input-node"
                                },
                                "examples": {
                                    "rich node example": {
                                        "value": {
                                            "node-id": "cloud-ncs540-1",
                                            "aliases": [
                                                "100.0.0.50",
                                                "cloud-ncs540-1.fqdn.com"
                                            ],
                                            "dims": {
                                                "city": "Boston",
                                                "state": "MA"
                                            }
                                        }
                                    }
                                }
                            }
                        },
                        "description": "node response"
                    }
                },
                "operationId": "createnode",
                "summary": "Create a node",
                "description": "Creates a new instance of a `node`."
            }
        },
        "/nodes/node={node-id}": {
            "get": {
                "tags": [
                    "nodes"
                ],
                "parameters": [
                    {
                        "name": "node-id",
                        "description": "The identifier for the link to read, update or delete.",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "default": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/error"
                                }
                            }
                        },
                        "description": "unexpected error"
                    },
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/output-node"
                                },
                                "examples": {
                                    "rich node example": {
                                        "value": {
                                            "node-id": "cloud-ncs540-1",
                                            "aliases": [
                                                "100.0.0.50",
                                                "cloud-ncs540-1.fqdn.com"
                                            ],
                                            "dims": {
                                                "city": "Boston",
                                                "state": "MA"
                                            }
                                        }
                                    }
                                }
                            }
                        },
                        "description": "nodes response"
                    }
                },
                "operationId": "getnode",
                "summary": "Get a node.",
                "description": "Gets the details of a single instance of a `node`."
            },
            "put": {
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/base-node"
                            }
                        }
                    },
                    "required": true
                },
                "tags": [
                    "nodes"
                ],
                "responses": {
                    "202": {
                        "description": "Success response"
                    }
                },
                "operationId": "updatenode",
                "summary": "Update a node.",
                "description": "Updates an existing `node`."
            },
            "delete": {
                "tags": [
                    "nodes"
                ],
                "parameters": [
                    {
                        "name": "node-id",
                        "description": "The identifier for the node to read, update or delete.",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "default": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/error"
                                }
                            }
                        },
                        "description": "unexpected error"
                    },
                    "204": {
                        "description": "node deleted"
                    }
                },
                "operationId": "deletenode",
                "summary": "Delete a node.",
                "description": "Deletes an existing `node`."
            },
            "parameters": [
                {
                    "name": "node-id",
                    "description": "The identifier for the node to read, update or delete.",
                    "schema": {
                        "type": "string"
                    },
                    "in": "path",
                    "required": true
                }
            ]
        },
        "/networks": {
            "summary": "Path used to manage the list of networks.",
            "description": "The REST endpoint/path used to list and create zero or more `network` entities.  This path contains a `GET` and `POST` operation to perform the list and create tasks, respectively.",
            "get": {
                "tags": [
                    "networks"
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "items": {
                                        "$ref": "#/components/schemas/output-network"
                                    }
                                }
                            }
                        },
                        "description": "Successful response - returns an array of `network` entities."
                    }
                },
                "operationId": "getnetworks",
                "summary": "List all networks",
                "description": "Gets a list of all `network` entities."
            },
            "post": {
                "requestBody": {
                    "description": "A new `network` to be created.",
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/input-network"
                            }
                        }
                    },
                    "required": true
                },
                "tags": [
                    "networks"
                ],
                "responses": {
                    "201": {
                        "description": "Successful response."
                    }
                },
                "operationId": "createnetwork",
                "summary": "Create a network",
                "description": "Creates a new instance of a `network`."
            }
        },
        "/networks/network={network-id}": {
            "summary": "Path used to manage a single network.",
            "description": "The REST endpoint/path used to get, update, and delete single instances of an `network`.  This path contains `GET`, `PUT`, and `DELETE` operations used to perform the get, update, and delete tasks, respectively.",
            "get": {
                "tags": [
                    "networks"
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/output-network"
                                }
                            }
                        },
                        "description": "Successful response - returns a single `network`."
                    }
                },
                "operationId": "getnetwork",
                "summary": "Get a network",
                "description": "Gets the details of a single instance of a `network`."
            },
            "put": {
                "requestBody": {
                    "description": "Updated `network` information.",
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/input-network"
                            }
                        }
                    },
                    "required": true
                },
                "tags": [
                    "networks"
                ],
                "responses": {
                    "202": {
                        "description": "Successful response."
                    }
                },
                "operationId": "updatenetwork",
                "summary": "Update a network",
                "description": "Updates an existing `network`."
            },
            "delete": {
                "tags": [
                    "networks"
                ],
                "responses": {
                    "204": {
                        "description": "Successful response."
                    }
                },
                "operationId": "deletenetwork",
                "summary": "Delete a network",
                "description": "Deletes an existing `network`."
            },
            "parameters": [
                {
                    "name": "network-id",
                    "description": "A unique identifier for a `network`.",
                    "schema": {
                        "type": "string"
                    },
                    "in": "path",
                    "required": true
                }
            ]
        }
    },
    "components": {
        "schemas": {
            "error": {
                "type": "string"
            },
            "dimensions": {
                "description": "Flexible dimension field.",
                "type": "object",
                "additionalProperties": {
                    "type": "string"
                }
            },
            "base-node": {
                "type": "object",
                "properties": {
                    "dims": {
                        "$ref": "#/components/schemas/dimensions",
                        "description": "Flexible dimension field."
                    },
                    "aliases": {
                        "description": "Alternate identifiers for this node.",
                        "type": "array",
                        "items": {
                            "type": "string"
                        }
                    }
                },
                "example": {
                    "node-id": "cloud-ncs540-1",
                    "network-id": "Default-network",
                    "aliases": [
                        "100.0.0.50",
                        "cloud-ncs540-1.fqdn.com"
                    ],
                    "dims": {
                        "city": "Boston",
                        "state": "MA"
                    }
                }
            },
            "network-input-link": {
                "description": "This is a link between two nodes",
                "type": "object",
                "allOf": [
                    {
                        "required": [
                            "link-id"
                        ],
                        "type": "object",
                        "properties": {
                            "link-id": {
                                "description": "The unique identifier for this link.",
                                "type": "string"
                            }
                        }
                    },
                    {
                        "$ref": "#/components/schemas/base-link"
                    }
                ],
                "example": {
                    "link-id": "cloud-ncs540-1 : HundredGigE0/0/0/28 : ron-8201-2 : HundredGigE0/0/0/26 : IPv4",
                    "source-node": "cloud-ncs540-1",
                    "dest-node": "ron-8201-2"
                }
            },
            "network-input-node": {
                "type": "object",
                "allOf": [
                    {
                        "required": [
                            "node-id"
                        ],
                        "type": "object",
                        "properties": {
                            "node-id": {
                                "description": "A unique identifier for a given node.",
                                "type": "string"
                            }
                        }
                    },
                    {
                        "$ref": "#/components/schemas/base-node"
                    }
                ],
                "example": {
                    "node-id": "cloud-ncs540-1",
                    "network-id": "Default-network",
                    "aliases": [
                        "100.0.0.50",
                        "cloud-ncs540-1.fqdn.com"
                    ],
                    "dims": {
                        "city": "Boston",
                        "state": "MA"
                    }
                }
            },
            "output-node": {
                "description": "",
                "type": "object",
                "allOf": [
                    {
                        "type": "object",
                        "properties": {
                            "neighbors": {
                                "description": "An array of node-ids for nodes which are neighbors to this node.",
                                "type": "array",
                                "items": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    {
                        "$ref": "#/components/schemas/input-node"
                    }
                ]
            },
            "output-link": {
                "description": "Information about a link in the topology.",
                "type": "object",
                "allOf": [
                    {
                        "required": [
                            "ordered-link-id"
                        ],
                        "type": "object",
                        "properties": {
                            "ordered-link-id": {
                                "description": "An ordered link identifier utilized for correlation.",
                                "type": "string"
                            }
                        }
                    },
                    {
                        "$ref": "#/components/schemas/input-link"
                    }
                ]
            },
            "network-output-link": {
                "description": "A link with VIA extensions, but no network id for use inside a full topology object.",
                "type": "object",
                "allOf": [
                    {
                        "required": [
                            "ordered-link-id"
                        ],
                        "type": "object",
                        "properties": {
                            "ordered-link-id": {
                                "description": "An ordered link identifier utilized for correlation.",
                                "type": "string"
                            }
                        }
                    },
                    {
                        "$ref": "#/components/schemas/network-input-link"
                    }
                ]
            },
            "network-output-node": {
                "description": "Base node returned with VIA extensions.",
                "type": "object",
                "allOf": [
                    {
                        "type": "object",
                        "properties": {
                            "neighbors": {
                                "description": "An array of node-ids for nodes which are neighbors to this node.",
                                "type": "array",
                                "items": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    {
                        "$ref": "#/components/schemas/network-input-node"
                    }
                ]
            },
            "output-network": {
                "description": "A network of links and nodes.",
                "required": [
                    "network-id"
                ],
                "type": "object",
                "properties": {
                    "links": {
                        "description": "The links in this network.",
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/network-output-link"
                        }
                    },
                    "nodes": {
                        "description": "The nodes of this network.",
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/network-output-node"
                        }
                    },
                    "network-id": {
                        "description": "The identifier for this network.",
                        "type": "string"
                    }
                }
            },
            "input-link": {
                "description": "A link with an associated network-id.",
                "type": "object",
                "allOf": [
                    {
                        "type": "object",
